mapper.update:
update title_examine
title_examine_id = #{id,jdbcType=INTEGER}, title_id = #{title_id,jdbcType=INTEGER}, applicant = #{applicant,jdbcType=INTEGER}, auditor = #{auditor,jdbcType=INTEGER}, status = #{status,jdbcType=VARCHAR}, ts = #{ts,jdbcType=VARCHAR}, dr = #{dr,jdbcType=INTEGER},
where id = #{id,jdbcType=INTEGER}

select.if:
SELECT e.id , t.title_name AS titleName, e.applicant AS applicantId, uu. NAME AS applyer, e.auditor AS auditorId, u. NAME AS author, CASE WHEN e.status='1' THEN '未审核' WHEN e.status='2'THEN '审核通过' ELSE '审核未通过' END flag FROM title_examine e JOIN title t ON e.title_id = t.id JOIN t_user u ON u.id = e.auditor AND u.id = #{authorId} JOIN t_user uu ON uu.id = e.applicant where 1=1
AND e.status = #{status}
AND t.title_name = #{titleName}
AND uu.NAME = #{applyer}

mapper.update:
update title_examine
set status = #{status},dr = 1
where id = #{id}

select.if:
SELECT b.title_name AS title_name, title_type AS title_type, title_source AS title_source, suitMajorName AS suitMajorName, suitScope AS suitScope, c. NAME AS NAME, a.id as id, CASE when a.status='1' THEN '未审核' WHEN a.status='2'THEN '审核通过' ELSE '审核未通过' END status FROM title_examine a JOIN title b ON a.title_id = b.id JOIN t_user c ON a.auditor = c.id WHERE applicant=#{id}
AND c.name like CONCAT('%',#{author},'%')
AND b.title_name like CONCAT('%',#{titlename},'%')
